pragma solidity ^0.8.0;

// solhint-disable-next-line interface-starts-with-i
interface AggregatorV3Interface {
  function decimals() external view returns (uint8);

  function description() external view returns (string memory);

  function version() external view returns (uint256);

  function getRoundData(
    uint80 _roundId
  ) external view returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound);

  function latestRoundData()
    external
    view
    returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound);
}
","keccak256":"0x257a8d28fa83d3d942547c8e129ef465e4b5f3f31171e7be4739a4c98da6b4f0","license":"MIT"},"contracts/PriceConvertor.sol":{"content":"//SPDX-License-Identifier: MIT


pragma solidity ^0.8.18;

//import contract ABI from github
import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

library PriceConvertor{
    //function to get pricefeed
    function getPrice(AggregatorV3Interface priceFeed) internal view returns (uint256){
        //Address 0x694AA1769357215DE4FAC081bf1f309aDC325306
        //get latest price feed
        (,int256 price,,,) = priceFeed.latestRoundData();

        return uint256(price * 1e18);
    }

    //convert eth amount to usd
    function getConversionRate(uint256 ethAmount,AggregatorV3Interface priceFeed) internal view returns (uint256){
        uint256 ethPrice = getPrice(priceFeed);
        uint256 ethAmountInUsd = (ethPrice * ethAmount) / 1e18;
        return ethAmountInUsd;
    }

    //function to getversion
    function getVersion() internal view returns(uint256){
        return AggregatorV3Interface(0x694AA1769357215DE4FAC081bf1f309aDC325306).version();
    }
} ","keccak256":"0xcc50d53f23b46ca2c1a593cca0c009d651b65e1906b85ab9db9aeb7d525c61d8","license":"MIT"}},"version":1}

